const familyItems = $('Get Family Data').all();
const zoneItems = $input.all();

const members = familyItems.map(item => {
  const d = item.json;
  let lastSeenFormatted = 'Nunca';
  if (d.last_seen) {
    const now = new Date();
    const lastSeen = new Date(d.last_seen);
    const diffMs = now - lastSeen;
    const diffMins = Math.floor(diffMs / 60000);
    const diffHours = Math.floor(diffMs / 3600000);
    const diffDays = Math.floor(diffMs / 86400000);

    if (diffMins < 1) lastSeenFormatted = 'Justo ahora';
    else if (diffMins < 60) lastSeenFormatted = 'Hace ' + diffMins + ' min';
    else if (diffHours < 24) lastSeenFormatted = 'Hace ' + diffHours + 'h';
    else lastSeenFormatted = 'Hace ' + diffDays + ' dias';
  }

  return {
    userId: d.user_id,
    name: d.name,
    role: d.role,
    deviceId: d.device_id,
    deviceName: d.device_name,
    isOnline: d.is_online || false,
    lastSeenFormatted: lastSeenFormatted,
    location: d.latitude ? {
      latitude: parseFloat(d.latitude),
      longitude: parseFloat(d.longitude),
      accuracy: d.accuracy,
      timestamp: d.location_timestamp
    } : null
  };
});

const safeZones = zoneItems.map(item => {
  const z = item.json;
  return {
    zoneId: z.zone_id,
    name: z.name,
    lat: parseFloat(z.center_lat),
    lng: parseFloat(z.center_lng),
    radius: z.radius_meters
  };
});

return [{ json: { members: members, safeZones: safeZones } }];
