const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
let code = '';
for (let i = 0; i < 6; i++) {
  code += chars.charAt(Math.floor(Math.random() * chars.length));
}
return [{ json: { familyName: $input.first().json.familyName, userName: $input.first().json.userName, inviteCode: code } }];

const row = $input.first().json;
return [{
  json: {
    success: true,
    userId: row.user_id,
    familyId: row.family_id,
    familyName: row.name,
    inviteCode: row.invite_code,
    role: row.role
  }
}];
