const locations = $input.all().map(item => {
  const l = item.json;
  return {
    locationId: l.location_id,
    deviceId: l.device_id,
    userId: l.user_id,
    userName: l.user_name,
    latitude: parseFloat(l.latitude),
    longitude: parseFloat(l.longitude),
    accuracy: l.accuracy,
    timestamp: l.timestamp
  };
});

return [{ json: { success: true, locations: locations, count: locations.length } }];
